import { Component } from "react";
import React from "react";
import { Link } from "react-router-dom";

class CardCatalogoEspecifico extends React.Component {
  state = {
    servico: null
  };

  constructor(servico) {
    super();
    this.state.servico = servico;
    this.nome = servico.nomeServico;
    //this.imagem = "data:image/png;base64," + servico.imagem[0].image;
    this.preco = servico.precoServico;
    this.descricao = servico.descricaoServico;
    this.id = servico.idServico;
  }

  render() {
    return (
      <div className="col-sm ml-3 mr-3 mt-4">
        <div className="card">
          <div className="card-body" align="center">
            <div className="row w-100 h-25 border border-secondary">
              <img src={this.props.imagem} />
            </div>
            <h5 className="card-title">{this.props.nome}</h5>
            <p className="card-text">{this.props.descricao}</p>
            <p>{this.props.preco}</p>
            <Link to="/DetalhesDoServico" className="btn btn-primary">
              Ver detalhes do id {this.props.id}
            </Link>
          </div>
        </div>
      </div>
    );
  }
}

export default CardCatalogoEspecifico;
